import api from "../../../utils/api";
// import styles from "./CreateTask.module.css";

import { useState, useEffect } from "react";

import { useNavigate } from "react-router-dom";

import useFlashMessage from "../../../hooks/useFlashMessage";
import TaskForm from "../../form/TaskForm";

function CreateTask() {
  const [token] = useState(localStorage.getItem("token") || "");
  const { setFlashMessage } = useFlashMessage();
  const navigate = useNavigate();

  useEffect(() => {
    api
      .get("/users/checkuser", {
        headers: {
          Authorization: `Bearer ${JSON.parse(token)}`,
        },
      })
      .then((response) => {
        setUser(response.data);
      });
  }, [token]);

  async function registerTask(task) {
    let msgType = "success";

    const formData = new FormData();

    const taskFormData = await Object.keys(task).forEach((key) => {
      formData.append(key, task[key]);
    });

    formData.append("task", taskFormData);

    const data = await api
      .post("/tasks/create/", formData, {
        Authorization: `Bearer ${JSON.parse(token)}`,
        "Content-Type": "multipart/form-data",
      })
      .then((response) => {
        return response.data;
      })
      .catch((err) => {
        msgType = "error";
        return err.response.data;
      });

    setFlashMessage(data.message, msgType);
    if (msgType !== "error") {
      navigate("/tasks/mytasks");
    }
  }

  return (
    <section>
      <h1>Add a Task</h1>
      <TaskForm handleSubmit={registerTask} btnText="Add Task" />
    </section>
  );
}

export default CreateTask;
